mapper.update:
update tb_file_info
set

    file_name = #{fileName}


    ,last_update_time = #{lastUpdateTime}


    ,file_pid = #{filePid}

where

    user_id = #{userId}


    and file_id = #{fileId}

update tb_file_info
set del_flag = #{fileInfo.delFlag}

    ,recovery_time = #{fileInfo.recoveryTime}


    ,last_update_time = #{lastUpdateTime}

where user_id = #{fileInfo.userId}

    and file_id in (#{item})


    and file_pid in (#{item})

update tb_file_info
set file_name = #{fileName}
where user_id = #{userId}
and file_id = #{fileId}

mapper.select:
select ifnull(sum(file_size), 0)
from tb_file_info
where user_id = #{userId}
